let textCache = {};
let currentTextSize = 32;
let currentTextAlign;

function textSize(size) {
  currentTextSize = size;
}

function textAlign(h, v) {
  currentTextAlign = [h, v];
}

// Memoized text rendering
function getTextTexture(txt) {
  const key = `${txt}|${currentTextSize}|${currentTextAlign[0]}|${currentTextAlign[1]}`;
  if (textCache[key]) return textCache[key];

  let g = createGraphics(1, 1); // dummy to measure text size
  g.textSize(currentTextSize);
  g.textAlign(...currentTextAlign);
  let w = g.textWidth(txt);
  let h = currentTextSize * 1.2;
  g.remove();

  // create actual buffer
  let buffer = createGraphics(ceil(w), ceil(h));
  buffer.pixelDensity(2);
  buffer.clear();
  buffer.textSize(currentTextSize);
  buffer.textAlign(...currentTextAlign);
  buffer.fill(255);
  buffer.text(txt, buffer.width / 2, buffer.height / 2);

  let img = buffer.get();
  buffer.remove();

  textCache[key] = {
    img,
    w: buffer.width,
    h: buffer.height
  };
  return textCache[key];
}

function text3D(txt, x, y, z, baseHeight = 0.03) {
  const tex = getTextTexture(txt);
  const aspect = tex.w / tex.h;
  const planeW = baseHeight * aspect;
  const planeH = baseHeight;

  push();
  translate(x, y, z);
  rotateX(PI);
  texture(tex.img);
  plane(planeW, planeH);
  pop();
}

